mapper.select:
		 select p_code, p.id as id, s.m_code as m_code, m_name, m_photo, p_friends, p_star, p_review, p_complete, p_photo, m_date, m_carbon, m_point, m_personnel,m_type, m_location
 	 from member m, participation p, mission s
   		where p.id =m.id and s.m_code = p.m_code and p.id=#{id} order by p_code desc;

mapper.insert:
      insert into participation (  id, m_code, p_friends)
      select #{id}, #{m_code}, #{p_friends}
from dual where not exists(select * from participation where id = #{id} and m_code = #{m_code});

mapper.update:

 update mission
set m_personnel = m_personnel - #{p_friends}
where m_code = #{m_code};


update member, ( SELECT g_code, g_start, g_end FROM grade) g
set grade = g.g_code
where id = #{id} and (carbon BETWEEN g.g_start AND g.g_end);


 update mission
set m_personnel = m_personnel + 1
where m_code = #{m_code};

mapper.select:
  select  p_code, p.id as id, s.m_code as m_code, m_name, m_photo, p_friends, m_date, p_complete, p_review, p_star, m_carbon, m_point, m_personnel, point, m_inform, carbon
	 from member m, participation p, mission s
   where p.id =m.id and s.m_code = p.m_code and p_code = #{p_code};

mapper.update:
update participation 
set p_complete = 1, p_photo = #{p_photo} 
where p_code = #{p_code};


 update member
set point = point + #{m_point} , carbon = carbon + #{m_carbon}
where id = #{id};

update participation 
set p_star = #{p_star}, p_review = #{p_review} 
where p_code = #{p_code};


 update participation
set p_redcard = #{p_redcard} + 1
where p_code = #{p_code};
